<!DOCTYPE html>
<html lang="pt-br">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Bandeirolas de São João</title>
    <style>
        body {
            margin: 0;
            overflow: hidden;
            background-color: #F8F9FA; /* Cor de fundo opcional */
        }

        .bandeirola {
            position: fixed;
            top: -50px;
            width: 20px;
            height: 20px;
            background-color: transparent;
            border-left: 10px solid transparent;
            border-right: 10px solid transparent;
            border-bottom: 20px solid;
            pointer-events: none;
            animation: fall linear infinite;
        }

        @keyframes fall {
            0% {
                transform: translateY(0) rotate(0deg);
            }
            100% {
                transform: translateY(100vh) rotate(360deg);
            }
        }
    </style>
</head>
<body>
    <!-- O conteúdo do seu site vai aqui -->

    <script>
        const bandeirolaColors = ['#FFC107', '#FF5722', '#8BC34A', '#03A9F4', '#E91E63', '#9C27B0'];

        function createBandeirola() {
            const bandeirola = document.createElement('div');
            bandeirola.classList.add('bandeirola');
            bandeirola.style.left = `${Math.random() * 100}vw`;
            bandeirola.style.borderBottomColor = bandeirolaColors[Math.floor(Math.random() * bandeirolaColors.length)];
            bandeirola.style.animationDuration = `${Math.random() * 3 + 2}s`;
            document.body.appendChild(bandeirola);

            setTimeout(() => {
                bandeirola.remove();
            }, 5000);
        }

        setInterval(createBandeirola, 100);
    </script>
</body>
</html>
